document.addEventListener("DOMContentLoaded",function(){chrome.storage.sync.get({attendance_taker:"who_gets_the_attendance@your_school.com",your_email:"your_email@address.com"},function(e){document.getElementById("attendance_taker").value=e.attendance_taker,document.getElementById("your_email").value=e.your_email}),document.getElementById("settings").addEventListener("submit",function(e){e.preventDefault(),chrome.storage.sync.set({attendance_taker:document.getElementById("attendance_taker").value,your_email:document.getElementById("your_email").value},function(){console.log("settings saved")})}),document.getElementById("select_from_drive").addEventListener("click",function(){chrome.identity.getAuthToken(function(e){alert(e)})})});